package Basics;

public class pyramidPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	
	for (int i = 0; i <= 10; i++) {
		// i=0 i=1; i=2 i=3 i=4; i=5;
		//System.out.println(i);
		for (int j =10;j>= i; j--) {
			System.out.print(" * ");
			//System.out.println(j);
		}
		System.out.println();
	}
	} 
}


